function recherch(e){var t=document.getElementById("contenu");t.innerHTML="",console.log(e),ajaxGet("https://api.github.com/users/"+e,function(e){var n=JSON.parse(e);console.log(n.login);var c=document.createElement("h2");c.textContent=n.login;var a=document.createElement("img");a.src=n.avatar_url;var r=document.createElement("h4");r.textContent=n.name;var o=document.createElement("p");o.textContent=n.company;var l=document.createElement("a");l.textContent=n.blog,l.href=n.blog,l.target="_blank",t.appendChild(c),t.appendChild(a),t.appendChild(r),t.appendChild(o),t.appendChild(l)})}document.getElementById("btnRechercher").addEventListener("click",function(){recherch(document.getElementById("inputRechercher").value)});